import React, { useEffect } from 'react';
import MainScreen from '../Components/MainScreen';
import Loading from "../Components/Loading";
import { Accordion, Badge, Button, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { fetchNotes, deleteNote } from '../notesSlice';

const MyNotes = () => {
  const dispatch = useDispatch();
  const { notes, loading, error } = useSelector((state) => state.notes);
  const { userInfo } = useSelector((state) => state.user);

  useEffect(() => {
    if (userInfo) {
      dispatch(fetchNotes());
    }
  }, [dispatch, userInfo]);

  const deleteHandler = (id) => {
    if (window.confirm("Are you sure you want to delete this note?")) {
      dispatch(deleteNote(id));
    }
  };

  if (!userInfo) {
    return <MainScreen title="Please log in to view your notes." />;
  }

  return (
    <MainScreen title={`Welcome back ${userInfo ? userInfo.name : ""}`}>
      <Link to="/createnote">
        <Button variant="primary" size="lg" className="mb-3">
          Create New Notes
        </Button>
      </Link>
      {loading && <Loading />}
      {error && <div className="text-danger">{error}</div>}
      {notes.length === 0 && !loading ? (
        <div className="text-center mt-4">No notes found. Create a new one!</div>
      ) : (
        <Accordion defaultActiveKey="0">
          {notes.map((note, index) => (
            <Accordion.Item eventKey={index} key={note._id}>
              <Card>
                <Card.Header className="d-flex justify-content-between align-items-center">
                  <Accordion.Header as="h5">{note.title}</Accordion.Header>
                  <div>
                    <Button
                      href={`/note/${note._id}`}
                      variant="outline-secondary"
                      size="sm"
                      className="me-2"
                    >
                      Edit
                    </Button>
                    <Button
                      variant="outline-danger"
                      size="sm"
                      onClick={() => deleteHandler(note._id)}
                    >
                      Delete
                    </Button>
                  </div>
                </Card.Header>
                <Accordion.Body>
                  <Card.Body>
                    <h6>
                      <Badge bg="success" className="mb-2">
                        Category - {note.category}
                      </Badge>
                    </h6>
                    <blockquote className="blockquote">
                      <p>{note.content}</p>
                      <footer className="blockquote-footer">
                        Created on - {new Date(note.createdAt).toLocaleDateString()}
                      </footer>
                    </blockquote>
                  </Card.Body>
                </Accordion.Body>
              </Card>
            </Accordion.Item>
          ))}
        </Accordion>
      )}
    </MainScreen>
  );
};

export default MyNotes;
